posts.each_with_index do |post, index|
  attributes = {
    title_en: post[:title][:en],
    title_ru: post[:title][:ru],
    title_ua: post[:title][:ua],
    title_de: post[:title][:de],
    description_en: post[:description][:en],
    description_ru: post[:description][:ru],
    description_ua: post[:description][:ua],
    description_de: post[:description][:de],
    year: post[:year]
  }

